interface GameState {
  rounds: Round[];
}

interface Round {
  p1: string;
  p2: string;
}

class Bot {
  dynamiteCount: number;
  enemyDynamiteCount: number;
  currentStrategy: Bot;
  allStrategies: Bot[];
  availableMoves: string[];
  rollover: number;
  scoreDifference: number;

  constructor() {
    this.dynamiteCount = 99;
    this.enemyDynamiteCount = 100;
    this.allStrategies = [
      new RandomBot(),
      new CopyLastMoveBot(),
      new BeatLastMoveBot(),
      new HMMBot(),
    ];
    this.currentStrategy = this.allStrategies[0];
    this.availableMoves = ["R", "P", "S", "W", "D"];
    this.rollover = 0;
    this.scoreDifference = 0;
  }

  updateAvailableMoves(
    // TODO: rename
    movePlayedLastRound: string,
    enemyMovePlayedLastRound: string
  ) {
    this.availableMoves = ["R", "P", "S", "W", "D"];

    if (movePlayedLastRound === "D") {
      this.dynamiteCount -= 1;
    }

    if (enemyMovePlayedLastRound === "D") {
      this.enemyDynamiteCount -= 1;
    }

    if (this.dynamiteCount === 0) {
      this.availableMoves = this.availableMoves.filter((m) => m !== "D");
    }

    if (this.rollover < 1 || this.enemyDynamiteCount < 40) {
      // TODO: this is a threshold to be set
      this.availableMoves = this.availableMoves.filter((m) => m !== "W");
    }
  }

  pickRandomFrom(array: (string | Bot)[]): any {
    // TODO: implement some weights (could be static or dynamic)
    return array[Math.floor(Math.random() * array.length)];
  }

  switchStrategy() {
    if (this.scoreDifference >= 50) {
      // TODO: this is a threshold to be set (make obvious we are losing 50) (implement round buffer) (use number of consecutive loses instead)
      this.currentStrategy = this.pickRandomFrom(this.allStrategies); // TODO: exclude the current strategy
    }
  }

  makeMove(gamestate: GameState): string {
    this.switchStrategy();

    if (gamestate.rounds.length > 0) {
      const lastRound = gamestate.rounds[gamestate.rounds.length - 1];
      this.updateAvailableMoves(lastRound.p1, lastRound.p2);
    }
    return this.currentStrategy.makeMove(gamestate);
  }
}

class RandomBot extends Bot {
  makeMove(gamestate: GameState): string {
    return this.pickRandomFrom(this.availableMoves);
  }
}

class CopyLastMoveBot extends Bot {
  makeMove(gamestate: GameState): string {
    return gamestate.rounds[gamestate.rounds.length - 1].p2;
  }
}

class BeatLastMoveBot extends Bot {
  getMovesThatBeat(move: string): string[] {
    switch (move) {
      case "R":
        return ["P", "D"];
      case "P":
        return ["S", "D"];
      case "S":
        return ["R", "D"];
      case "W":
        return ["R", "P", "S"];
      case "D":
        return ["W"];
      default:
        return ["R", "P", "S", "W", "D"];
    }
  }

  makeMove(gamestate: GameState): string {
    const lastMove = gamestate.rounds[gamestate.rounds.length - 1].p2;
    const movesThatBeat = this.getMovesThatBeat(lastMove).filter((m) =>
      this.availableMoves.includes(m)
    );

    if (movesThatBeat.length > 0) {
      return this.pickRandomFrom(movesThatBeat);
    }

    return this.pickRandomFrom(["R", "P", "S"]);
  }
}

class HMMBot extends Bot {
  resultOfRound(round: Round): string {
    switch (round.p1) {
      case "R":
        switch (round.p2) {
          case "R":
            return "d";
          case "P":
          case "D":
            return "l";
          case "S":
          case "W":
            return "w";
        }
      case "P":
        switch (round.p2) {
          case "P":
            return "d";
          case "S":
          case "D":
            return "l";
          case "R":
          case "W":
            return "w";
        }
      case "S":
        switch (round.p2) {
          case "S":
            return "d";
          case "R":
          case "D":
            return "l";
          case "P":
          case "W":
            return "w";
        }
      case "W":
        switch (round.p2) {
          case "W":
            return "d";
          case "R":
          case "P":
          case "S":
            return "l";
          case "D":
            return "w";
        }
      case "D":
        switch (round.p2) {
          case "R":
          case "P":
          case "S":
            return "w";
          case "W":
            return "l";
          case "D":
            return "d";
        }
      default:
        return "w";
    }
  }

  getResultHistory(gamestate: GameState): string[] {
    let resultHistory = [];

    for (let round of gamestate.rounds) {
      resultHistory.push(this.resultOfRound(round));
    }

    return resultHistory;
  }

  probAndArgMax(probToMax: any, moveSet: string[]) {
    let maxProb = Number.NEGATIVE_INFINITY;
    let maxTag = null;

    for (let prevTag of moveSet) {
      let prob = probToMax(prevTag);

      if (prob > maxProb) {
        maxProb = prob;
        maxTag = prevTag;
      }
    }

    return [maxProb, maxTag];
  }

  recoverMoves(backpointers: any) {
    let tagList = [];
    let currTag = EOS_TAG;

    // -- Reconstruct Moves Sequence from  Backpointers -- //
    for (let i = backpointers.length - 1; i > 0; i--) {
      currTag = backpointers[i][currTag];
      tagList.push(currTag);
    }

    return tagList.reverse();
  }

  viterbi(
    resultHistory: string[],
    moveSet: string[],
    transitionLogProbs: any,
    emissionLogProbs: any
  ) {
    // cn change any to interface

    const length = resultHistory.length;

    // -- Build Data Structures -- //
    let v: any[] = [];
    for (let i = 0; i < length + 1; i++) v.push({});

    let backpointers: any[] = [];
    for (let i = 0; i < length + 1; i++) backpointers.push({});

    // -- Initialisation Step -- //
    for (const tag of moveSet) {
      v[0][tag] =
        transitionLogProbs[SOS_TAG][tag] +
        emissionLogProbs[tag][resultHistory[0]];
      backpointers[0][tag] = SOS_TAG;
    }

    // -- Forward Propagation Step -- //
    for (let i = 1; i < length; i++) {
      for (let currTag of moveSet) {
        let probToMax = (prevTag: string) =>
          v[i - 1][prevTag] +
          transitionLogProbs[prevTag][currTag] +
          emissionLogProbs[currTag][resultHistory[i]];

        [v[i][currTag], backpointers[i][currTag]] = this.probAndArgMax(
          probToMax,
          moveSet
        );
      }
    }

    // -- Final Step -- //
    let probToMax = (prevTag: string) =>
      v[length - 1][prevTag] + transitionLogProbs[prevTag][EOS_TAG];
    [v[length][EOS_TAG], backpointers[length][EOS_TAG]] = this.probAndArgMax(
      probToMax,
      moveSet
    );

    // -- Return Moves -- //
    return this.recoverMoves(backpointers);
  }

  makeMove(gamestate: GameState): string {
    let resultHistory = this.getResultHistory(gamestate);
    resultHistory = resultHistory.slice(
      resultHistory.length - 5,
      resultHistory.length
    );
    resultHistory.push("w");

    const moves = this.viterbi(
      resultHistory,
      TAG_SET,
      TRANSITION_PROBS,
      EMISSION_PROBS
    );

    let move = moves[moves.length - 1][0];
    // let enemyMove = moves[moves.length - 1][1]; (TODO: future functionality)

    if (Math.random() < 0.2 || !this.availableMoves.includes(move)) {
      move = this.pickRandomFrom(this.availableMoves);
    }

    return move;
  }
}

